import React, { useEffect, useState, useRef } from "react";

const Panel = ({ children }) => {
  const ref = useRef(null);

  const refTop = useRef(null);
  const refRight = useRef(null);
  const refBottom = useRef(null);
  const refLeft = useRef(null);

  const refTopRight = useRef(null);
  const refTopLeft = useRef(null);
  const refBottomRight = useRef(null);
  const refBottomLeft = useRef(null);

  useEffect(() => {
    // top
    const panel = ref.current;
    const styles = window.getComputedStyle(panel);
    let width = parseInt(styles.width, 10);
    let height = parseInt(styles.height, 10);
    let y = 0;
    let x = 0;
    const onMouseDownHandleTop = (event) => {
      console.log("skeet");
      y = event.clientY;
      x = event.clientX;
      console.log(x, y);
      document.addEventListener("mousedown", onMouseDragHandleTop);
      document.addEventListener("mouseup", onMouseUpHandleTop);
    };

    const onMouseDragHandleTop = (event) => {
      console.log(panel.style.height, panel.style.top);
      panel.style.height = styles.height + (y - event.clientY) + "px";
      panel.style.top = styles.top - (y - event.clientY) + "px";
    };

    const onMouseUpHandleTop = (event) => {};

    //right
    const onMouseDownHandleRight = (event) => {};

    const onMouseDragHandleRight = (event) => {};

    const onMouseUpHandleRight = (event) => {};

    //bottom
    const onMouseDownHandleBottom = (event) => {};

    const onMouseDragHandleBottom = (event) => {};

    const onMouseUpHandleBottom = (event) => {};

    //left
    const onMouseDownHandleLeft = (event) => {};

    const onMouseDragHandleLeft = (event) => {};

    const onMouseUpHandleLeft = (event) => {};

    // corners
    const onMouseDownCornerDrag = (event) => {};

    const onMouseDragCornerDrag = (event) => {};

    const onMouseUpCornerDrag = (event) => {};

    const handleTop = refTop.current;
    handleTop.addEventListener("mousedown", onMouseDownHandleTop);
    const handleLeft = refLeft.current;
    handleLeft.addEventListener("mousedown", onMouseDownHandleLeft);
    const handleBottom = refBottom.current;
    handleBottom.addEventListener("mousedown", onMouseDownHandleBottom);
    const handleRight = refRight.current;
    handleRight.addEventListener("mousedown", onMouseDownHandleRight);

    const handleTopRight = refTopRight.current;
    handleTopRight.addEventListener("mousedown", onMouseDownCornerDrag);
    const handleTopLeft = refTopLeft.current;
    handleTopLeft.addEventListener("mousedown", onMouseDownCornerDrag);
    const handleBottomRight = refBottomRight.current;
    handleBottomRight.addEventListener("mousedown", onMouseDownCornerDrag);
    const handleBottomLeft = refBottomLeft.current;
    handleBottomLeft.addEventListener("mousedown", onMouseDownCornerDrag);

    return () => {
      handleTop.removeEventListener("mousedown", onMouseDownHandleTop);
      handleTop.removeEventListener("mousedown", onMouseDownHandleLeft);
      handleTop.removeEventListener("mousedown", onMouseDownHandleBottom);
      handleTop.removeEventListener("mousedown", onMouseDownHandleRight);

      handleTop.removeEventListener("mousedown", onMouseDownCornerDrag);
      handleTop.removeEventListener("mousedown", onMouseDownCornerDrag);
      handleTop.removeEventListener("mousedown", onMouseDownCornerDrag);
      handleTop.removeEventListener("mousedown", onMouseDownCornerDrag);
    };
  }, []);
  return (
    <div ref={ref} className="panel">
      <div className="header"></div>
      <div className="content">{children}</div>

      <div ref={refTop} className="resize handle-top"></div>
      <div ref={refLeft} className="resize handle-right"></div>
      <div ref={refBottom} className="resize handle-bottom"></div>
      <div ref={refRight} className="resize handle-left"></div>
      <div ref={refTopRight} className="resize corner top-right"></div>
      <div ref={refTopLeft} className="resize corner top-left"></div>
      <div ref={refBottomRight} className="resize corner bottom-right"></div>
      <div ref={refBottomLeft} className="resize corner bottom-left"></div>
    </div>
  );
};
export default Panel;
