import React, { useState } from "react";

const Panel = ({ children }) => {
  const [initialPos, setInitialPos] = React.useState(null);
  const [initialSize, setInitialSize] = React.useState(null);

  const onClickRight = (e) => {
    console.log("yeet");
    let resizable = document.getElementById("right");
    let panel = document.getElementById("panel");
    setInitialPos(e.clientX);
    setInitialSize(panel.width);
  };

  const resizeRight = (e) => {
    let resizable = document.getElementById("panel");

    resizable.style.width = `${
      parseInt(initialSize) + parseInt(e.clientX - initialPos)
    }px`;
  };
  return (
    <div id="panel" className="panel">
      <div className="header"></div>
      <div className="content">{children}</div>
      <div className="resize handle-top"></div>
      <div className="resize handle-left"></div>
      <div className="resize handle-bottom"></div>
      <div
        id="right"
        draggable="true"
        onDragStart={onClickRight}
        onDrag={resizeRight}
        className="resize handle-right"
      ></div>
      <div className="resize corner top-right"></div>
      <div className="resize corner top-left"></div>
      <div className="resize corner bottom-right"></div>
      <div className="resize corner bottom-left"></div>
    </div>
  );
};
export default Panel;
