import React, { useState, useEffect } from "react";

const PanelHandle = ({ onDrag }) => {
  const [mouseDown, setMouseDown] = useState(false);
  useEffect(() => {
    const handleMouseUp = () => setMouseDown(false);

    window.addEventListener("mousedown", handleMouseUp);

    return () => {
      window.addEventListener("mousedown", handleMouseUp);
    };
  }, []);
  useEffect(() => {
    const handleMouseMove = (e) => onDrag(e.movementX, e.movementY);
    if (mouseDown) {
      window.addEventListener("mousemove", handleMouseMove);
    }
    return () => {
      window.removeEventListener("mousemove", handleMouseMove);
    };
  }, []);
  const handleMouseDown = setMouseDown(true);
  return (
    <div className="panel-handle" onMouseDown={handleMouseDown}>
      title
    </div>
  );
};
export default PanelHandle;
