import React, { useState } from "react";
import "./App.css";
import Panel from "./Panel";
function App() {
  const [foremostIndex, setForemostIndex] = useState(1);
  const updateIndex = (id) => {
    setForemostIndex(foremostIndex + 1);
    document.getElementById(`panel-${id}`).style.zIndex = foremostIndex;
  };
  return (
    <div className="App">
      <Panel updateIndex={updateIndex} id={1}></Panel>
      <Panel updateIndex={updateIndex} id={2}></Panel>
      <Panel updateIndex={updateIndex} id={3}></Panel>
      <Panel updateIndex={updateIndex} id={4}></Panel>
    </div>
  );
}

export default App;
