import React, { useEffect, useState } from "react";

const Panel = ({ children }) => {
  const handleMouseDown = (direction) => {
    console.log(direction);
  };
  return (
    <div id="panel" className="panel">
      <div className="header"></div>
      <div className="content">{children}</div>

      <div
        onMouseDown={console.log("yeet")}
        className="resize handle-top"
      ></div>

      <div
        onMouseDown={handleMouseDown("left")}
        className="resize handle-left"
      ></div>

      <div
        onMouseDown={handleMouseDown("bottom")}
        className="resize handle-bottom"
      ></div>

      <div
        onMouseDown={handleMouseDown("right")}
        className="resize handle-right"
      ></div>

      <div
        onMouseDown={handleMouseDown("top-right")}
        className="resize corner top-right"
      ></div>
      <div
        onMouseDown={handleMouseDown("top-left")}
        className="resize corner top-left"
      ></div>
      <div
        onMouseDown={handleMouseDown("bottom-right")}
        className="resize corner bottom-right"
      ></div>
      <div
        onMouseDown={handleMouseDown("bottom-left")}
        className="resize corner bottom-left"
      ></div>
    </div>
  );
};
export default Panel;
