import { useState } from "react";
import Clock from "./Clock";
function Taskbar({ children }) {
  const [count, setCount] = useState(1);
  const startButtonEasterEgg = () => {
    setCount(count + 1);
    if (count === 3) {
      document.getElementsByName("body").style.backgroundImage =
        "url('./asset/icon/xp.webp')";
    }
  };
  return (
    <div className="task-bar">
      <button onClick={(event) => startButtonEasterEgg} id="start-button">
        <img
          className="start-icon"
          src={require("./assets/start.png")}
          alt="10"
        ></img>
        <div className="start-text">start</div>
      </button>
      <div className="divider-long"></div>
      <div className="divider-fat"></div>
      <div className="tab-container">{children}</div>
      <div className="tray right-taskbar">
        <img
          className="start-icon"
          src={require("./assets/tray1.png")}
          alt="10"
        ></img>
        <img
          className="start-icon"
          src={require("./assets/tray2.png")}
          alt="10"
        ></img>
        <Clock />
      </div>
    </div>
  );
}

export default Taskbar;
